const reverseString = function(string) {
  return string.split("").reverse().join("")
 };

// Do not edit below this line
module.exports = reverseString;

// string.split("") - splits a string into an array of individual characters (example: "hello" turns into ["h", "e", "l", "l", "o"])

// string.reverse() - reverses an array

// string.join("") - joins the array of characters into a string

// https://www.samanthaming.com/tidbits/83-4-ways-to-convert-string-to-character-array/ and https://sebhastian.com/javascript-array-string/